        // Register event handlers.
        m_client.ConnectionAttempt += m_client_ConnectionAttempt;
        m_client.ConnectionEstablished += m_client_ConnectionEstablished;
        m_client.ConnectionTerminated += m_client_ConnectionTerminated;
